INSERT INTO `course` VALUES (124, 179, 'Ganyu là nhân vật cuối cùng của phiên bản 1.2 “Vôi trắng và rồng đen” được nhà phát triển Mihoyo giới thiệu đến cộng đồng. Ngay từ những ngày đầu tiên, cô nàng Ganyu này đã nhanh chóng chinh phục trái tim game thủ Genshin Impact với ngoại hình cũng như tính cách dễ mến, mặc dù luôn chìm đắm trong công việc và hay quên.', b'0', 'Demo 124', 770918, 0, 0, '2021-09-21 01:41:23', 1, 58, 1, '2021-05-19 15:35:12', b'1', 0, 0, 1263800.00, 39, '');
